    localCopyEdited: "star-and-times/The_St__Louis_Star_and_Times_Sat__Jan_4__1919_.jpg",
    localCopyFull: "star-and-times/The_St__Louis_Star_and_Times_Sat__Jan_4__1919_ (1).jpg",
